args:
/* --- Teacher Dashboard Content Editor (Dynamic) --- */
.section-item {
    border: 2px solid var(--primary-color);
    margin-bottom: 20px;
    background: var(--card-bg);
    border-radius: 8px;
    overflow: hidden;
    transition: background-color 0.3s, border-color 0.3s;
}

.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
    background: #eee;
    padding: 10px;
    color: var(--text-color);
}

.subsection-item {
    background: #f9f9f9;
    padding: 15px;
    margin-bottom: 15px;
    border-radius: 8px;
    border: 1px solid #ddd;
}

.subsection-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
    border-bottom: 1px solid #eee;
    padding-bottom: 5px;
    color: var(--text-color);
}

.video-item {
    background: white;
    padding: 8px;
    margin-bottom: 5px;
    border-radius: 4px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border: 1px solid #eee;
    color: var(--text-color);
}

.add-subsection-form {
    padding: 10px;
    background: #f0f0f0;
    border-radius: 5px;
    margin: 10px;
}

.add-video-form {
    margin-top: 10px; 
    padding-top: 10px; 
    border-top: 1px dashed #ccc;
}

/* Dark Mode Overrides for Dynamic Content */
body.dark-mode .section-item {
    background: var(--card-bg);
    border-color: #444;
}

body.dark-mode .section-header {
    background: #404058;
    color: #fff;
}

body.dark-mode .subsection-item {
    background: #2d2d44;
    border-color: #444;
}

body.dark-mode .subsection-header {
    border-bottom-color: #444;
    color: #fff;
}

body.dark-mode .video-item {
    background: #404058;
    border-color: #555;
    color: #fff;
}

body.dark-mode .add-subsection-form {
    background: #2d2d44;
    border: 1px solid #444;
}

body.dark-mode .add-video-form {
    border-top-color: #555;
}

body.dark-mode .section-item h3,
body.dark-mode .section-item h4,
body.dark-mode .section-item span {
    color: #fff !important;
}
